import{_ as s,c as a,o as t,j as e,a as i}from"./chunks/framework.DTR-fORK.js";const b=JSON.parse('{"title":"SAE101","description":"","frontmatter":{},"headers":[],"relativePath":"comprendre/SAE101.md","filePath":"comprendre/SAE101.md"}'),n={name:"comprendre/SAE101.md"},r=e("h1",{id:"sae101",tabindex:"-1"},[i("SAE101 "),e("a",{class:"header-anchor",href:"#sae101","aria-label":'Permalink to "SAE101"'},"​")],-1),o=e("p",null,"La SAE101 était un projet au cours duquel nous devions réaliser un audit numérique de cinq musées de notre choix. Notre objectif principal était de comparer ces musées sur plusieurs critères, afin de fournir une analyse complète et détaillée de leurs pratiques en ligne. Le projet d'audit était divisé en différentes parties, attribuées à chaque membre. J'avais la responsabilité d'analyser les stratégies et l'utilisation des réseaux sociaux par les différents musées.Mon rôle consistait à évaluer comment chaque musée utilisé différemment les réseaux sociaux et il adaptait leur présence en ligne pour atteindre un certain public.",-1),u=e("p",null,"Pendant mon analyse, j'ai pu observer des différences dans l'approche des musées vis-à-vis des réseaux sociaux. Certains musées cherchaient à établir une proximité avec leur communauté en utilisant les réseaux sociaux de manière interactive et engageante. Leur présence en ligne était caractérisée par des publications fréquentes, des réponses rapides aux commentaires et une volonté de partager du contenu intéressant et varié.",-1),c=e("p",null,"D'autres musées semblaient adopter une approche plus distante vis-à-vis des réseaux sociaux, peut-être en raison de ressources limitées ou d'une compréhension moins approfondie de l'importance des médias sociaux dans la communication contemporaine. Leurs profils étaient moins actifs, avec des publications plus espacées et une interaction limitée avec leur communauté en ligne.",-1),l=e("p",null,[e("img",{src:"http://u0oos4ks0goc08okoksc04s0.147.79.118.50.sslip.io/resize?image=/2af491040e35bec5181185157167fc2c.png&width=1920&height=1000",alt:"SAE101"})],-1),d=[r,o,u,c,l];function p(m,f,h,_,x,v){return t(),a("div",null,d)}const E=s(n,[["render",p]]);export{b as __pageData,E as default};
